function burgerMenu(){const e=document.querySelector(".burger"),s=document.querySelector(".menu"),c=document.querySelector("body");e.addEventListener("click",(()=>{s.classList.contains("active")?(s.classList.remove("active"),e.classList.remove("active-burger"),c.classList.remove("locked")):(s.classList.add("active"),e.classList.add("active-burger"),c.classList.add("locked"))})),window.addEventListener("resize",(()=>{window.innerWidth>767.98&&(s.classList.remove("active"),e.classList.remove("active-burger"),c.classList.remove("locked"))}))}function fixedNav(){const e=document.querySelector("nav");window.scrollY>=1?e.classList.add("fixed__nav"):e.classList.remove("fixed__nav")}burgerMenu(),window.addEventListener("scroll",fixedNav);